@story.route('/random_story', methods=['GET'])
def random_story():
    try:
        if not stories:
            return jsonify({"error": "No stories available"}), 404

        story = random.choice(stories)
        return jsonify({"story": story}), 200
    except Exception as e:
        return jsonify({"error": str(e)}), 500
